    import React,{useState} from 'react';
    import Axios from "axios";
  
    function Login(){


        
        const [uname,Setuname]=useState('');
        const [pass,Setpass]=useState('');
        const [validate,setValidate]=useState('');

        function refreshPage() {
            window.location.reload(false);
          }


        const submit = function(){
            Axios.post('http://localhost:3001/Admin',
            {uname:uname,pass:pass}).then((Response)=>{
                console.log(Response);
                setValidate(Response.data.message);

                if(Response.data.message=="Success!"){
                    window.location.href="/VerifiedAdmin"
                }

                else{
                    alert(Response.data.message);
                }
                
            });
        }

        return (<div class="login">

        <div class="body">
        <div class="row">
            <div class="col-lg-3 col-md-2"></div>
            <div class="col-lg-6 col-md-8 login-box">
                <div class="col-lg-12 login-key">
                </div>
                <div class="col-lg-12 login-title">
                    ADMIN PANEL
                </div>
    
                <div class="col-lg-12 login-form">
                    <div class="col-lg-12 login-form">
                        <form>
                            <div class="form-group">
                                <label class="form-control-label">USERNAME</label>
                                <input type="text" class="form-control" onChange={function(e){
                 Setuname(e.target.value)
             }}/>
                            </div>
                            <div class="form-group">
                                <label class="form-control-label">PASSWORD</label>
                                <input type="password" class="form-control" onChange={function(e){
                 Setpass(e.target.value)
             }}/>
                            </div>
    
                            <div class="col-lg-12 loginbttm">
                                <div class="col-lg-6 login-btm login-text">
                                </div>
                                <div class="col-lg-6 login-btm login-button">
                                    <button type="submit" class="btn btn-outline-primary" onClick={function(e){submit()}}>LOGIN</button>
                                </div>
                            </div>
                        </form>
                    </div>
                </div>
                <div class="col-lg-3 col-md-2"></div>
            </div>
        </div>
        </div>
        </div>
    );
    }
    
    export default Login;
