    import React,{useState,useEffect} from 'react';
    import Axios from "axios";
    import { Link } from 'react-router-dom';
    import {BrowserRouter as Router, Route, Routes} from "react-router-dom";
  
    function Login(){

        const [uname,Setuname]=useState('');
        const [pass,Setpass]=useState('');
        const [validate,setValidate]=useState('');

        
        useEffect(function(){
            Axios.get('http://localhost:3001/Admin').then((Response)=>{
              console.log(Response);
              setValidate(Response.data);
            });
          })

        const submit = function(){
            Axios.post('http://localhost:3001/Admin',
            {uname:uname,pass:pass});
            if(validate=="t"){
                <Router><Routes><Route path="/Validated">
                    <Admin/>
                </Route>
                </Routes>
                </Router>
            }
            else{
                console.log("err");
            }

        
        }


        return (<div class="login">

        <div class="body">
        <div class="row">
            <div class="col-lg-3 col-md-2"></div>
            <div class="col-lg-6 col-md-8 login-box">
                <div class="col-lg-12 login-key">
                    <i class="fa fa-key" aria-hidden="true"></i>
                </div>
                <div class="col-lg-12 login-title">
                    ADMIN PANEL
                </div>
    
                <div class="col-lg-12 login-form">
                    <div class="col-lg-12 login-form">
                        <form>
                            <div class="form-group">
                                <label class="form-control-label">USERNAME</label>
                                <input type="text" class="form-control" onChange={function(e){
                 Setuname(e.target.value)
             }}/>
                            </div>
                            <div class="form-group">
                                <label class="form-control-label">PASSWORD</label>
                                <input type="password" class="form-control" i onChange={function(e){
                 Setpass(e.target.value)
             }}/>
                            </div>
    
                            <div class="col-lg-12 loginbttm">
                                <div class="col-lg-6 login-btm login-text">
                                </div>
                                <div class="col-lg-6 login-btm login-button">
                                    <button type="submit" class="btn btn-outline-primary" onClick={function(){
                submit();
            }}>LOGIN</button>
                                </div>
                            </div>
                        </form>
                    </div>
                </div>
                <div class="col-lg-3 col-md-2"></div>
            </div>
        </div>
        </div>
        </div>
    );
    }
    
    export default Login;
